#This block filters out sequences with incorrect lengths (i.e indels, contamination). Very stringent approach, but typically will retain ~90% of the data.
print('filtering sequences...')
with open("{}.filtered.fasta".format(args.string), "w") as handle:
    for seq_record in SeqIO.parse("{}.fasta".format(args.string), "fasta"):
        lengths[len(seq_record)] += 1
        if len(seq_record) == Length_L:
            SeqIO.write(seq_record, handle, "fasta")

with open('{}.lengths.csv'.format(args.string), 'w') as f:
    writer = csv.writer(f)
    for row in lengths.items():
        writer.writerow(row)


#This block corrects the reading frame to ensure it is correctly tanslated.
#Orientation is defined based on the first two codon sequences, if both do not match it takes the reverse complement of the sequence.
sequences = defaultdict(int)
print('orienting sequences...')
with open("{}.reversed.fasta".format(args.string), "w") as handle:
    for seq_record in SeqIO.parse("{}.filtered.fasta".format(args.string), "fasta"):
        #print(seq_record.seq[0:3])
        if str(seq_record.seq[0:3]) not in Strt and str(seq_record.seq[4:6]) not in Strt2:
            SeqIO.write(seq_record.reverse_complement(id=True, name=True, description=True), handle, "fasta")
            myseq = seq_record.reverse_complement().seq
            sequences[str(myseq)] +=1
        else:
            SeqIO.write(seq_record, handle, "fasta")
            myseq = seq_record.seq
            sequences[str(myseq)] += 1

with open('{}.csv'.format(args.string), 'w') as f:
    writer = csv.writer(f)
    for row in sequences.items():
        writer.writerow(row)


#Next Step
print('translating sequences...')

#Translates the correctly orientated DNA sequences to protein sequences
proteins = (make_protein_record(nuc_rec) for nuc_rec in \
            SeqIO.parse("{}.reversed.fasta".format(args.string), "fasta"))
SeqIO.write(proteins, "{}.translated.fasta".format(args.string), "fasta")

#Next step
print('counting variants...')

#Function used to count variants.
#Inputs the fasta file of translated sequences, the code then loops over every sequence in the file and determines if it is
#a mutant or WT. Then it counts +1 for either WT (across all codons) or to the specific variant at the appropriate position.
for seq_record in SeqIO.parse("{}.translated.fasta".format(args.string), "fasta"):  #opens the fasta file and begins loop
    s1 = str(seq_record.seq)  #pulls out the protein sequence
    indx = [i for i in range(len(s1)) if s1[i] != WT[i]] #loops through each position of the protein and compares to WT sequence
                                                         #if different from WT, it keeps track of the variant and position in a list
    if len(indx) <= 1 and indx:   #if the list is length of 1 or less and the list is populated it will count it
        key1 = str("codon{}".format(str(indx[0]+n)))
        key2 = str(seq_record.seq[indx[0]])
        codons[key1][key2] += 1
    if len(indx) <= 1 and not indx:  #if list is empty (a WT sequence would be empty) it counts it as WT
        pos = 0 + pos_indx
        for x in WT:
            pos += 1
            key1 = str("codon{}".format(pos))
            key2 = x
            codons[key1][key2] += 1

df = pd.DataFrame(codons)
df.rename( columns={'Unnamed: 0':'Amino Acid'}, inplace=True )

df.to_csv("{}.raw_counts.csv".format(args.string), index=True)
